mapper.select:
SELECT t.* FROM JY_WORK_RULE t WHERE 1=1
AND t.id=#{id}
AND t.title LIKE CONCAT('%',#{param.name},'%')

SELECT t.id, t.year, t.workDate, t.status, t.name FROM JY_WORK_RULE t WHERE 1=1
AND t.name LIKE CONCAT('%',#{param.name},'%')
AND t.year=#{param.year}
AND t.name LIKE CONCAT('%',#{param.keyWord},'%')
AND t.status=#{param.status}
ORDER BY t.workDate

