mapper.select:
SELECT t.* FROM TASK_INFO t WHERE 1=1

    AND t.id=#{id}


    AND t.taskId=#{taskid}


    AND t.businessKey=#{businesskey}

SELECT t.* FROM TASK_INFO t WHERE 1=1

    AND t.id=#{param.id}



    AND (
    t.name LIKE CONCAT('%',#{param.keyWord},'%')

    )

order by t.createTime desc

SELECT t.* FROM TASK_INFO t WHERE 1=1

    AND t.id=#{param.id}


    AND t.assignee=#{param.assignee}

AND t.attr2 IS NOT NULL
order by t.createTime desc

select distinct businessKey,presentationSubject,createTime,attr1, processInstanceid,processDefinitionid from TASK_INFO t where 1=1
AND t.creator=#{param.creator}
order by t.createTime desc

mapper.delete:
DELETE FROM TASK_INFO
WHERE id IN(

    #{o.id}

)
